display Description255 diagnosisDescriptionWithCode()
{
    return strFmt("@MAZ924", this.Description, this.Code);
}

/// <summary>
///    Determines whether the specified record is located in the <c>HMDiagnosisDictionary</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMDiagnosisDictionary</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMDiagnosisDictionary</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMDiagnosisDictionary::exist(_recId))
    {
        ret = checkFailed(strfmt(HMDiagnosisDictionary::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMDiagnosisDictionary</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMDiagnosisDictionary</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 hmDiagnosisDictionary
             where
                 hmDiagnosisDictionary.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///     Finds the specified record in the <c>HMDiagnosisDictionary</c> table.
/// </summary>
/// <param name="_recid">
///     The record ID of the <c>HMDiagnosisDictionary</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMDiagnosisDictionary</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMDiagnosisDictionary find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMDiagnosisDictionary  hmDiagnosisDictionary;

    hmDiagnosisDictionary.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmDiagnosisDictionary.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmDiagnosisDictionary where hmDiagnosisDictionary.RecId == _recid;

    return hmDiagnosisDictionary;
}

/// <summary>
///     Finds the specified record in the <c>HMDiagnosisDictionary</c> table.
/// </summary>
/// <param name="_code">
///     The code of the <c>HMDiagnosisDictionary</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMDiagnosisDictionary</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMDiagnosisDictionary findByCode(
HMCode               _code,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMDiagnosisDictionary   diagnosisDictionary;

    diagnosisDictionary.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        diagnosisDictionary.concurrencyModel(_concurrencyModel);
    }

    select firstonly RecId from diagnosisDictionary where diagnosisDictionary.Code == _code;

    return diagnosisDictionary;
}

public client static void lookupDiagnosisCode(FormStringControl _diagnosisCodeSetStringControl)
{
    Args e;
    FormRun formRun;

    if (_diagnosisCodeSetStringControl!=null)
    {
        e=new Args();
        e.name(formStr(HMDiagnosisCodeLookup));
       e.lookupField(fieldNum(HMDiagnosisDictionary,Code));
        e.lookupValue(_diagnosisCodeSetStringControl.valueStr());
        e.caller(_diagnosisCodeSetStringControl);

        formRun = classFactory.formRunClass(e);
        formRun.init();

       _diagnosisCodeSetStringControl.performFormLookup(formRun);
    }

}

/// <summary>
///    Gets the message to use when a specific record in the <c>HMDiagnosisDictionary</c> does not exist.
/// </summary>
/// <returns>
///    The message that states a record does not exist.
/// </returns>
public static TxtNotExist txtNotExist()
{
    return "@SYS127989";
}
