public void delete()
{
    HMPatientInsurance HMPatientInsurance;
    HMPatient HMPatient;


    select *from HMPatientInsurance where HMPatientInsurance.HMInsuranceContractClassOrPlans == this.RecId;
    if(HMPatientInsurance)
    {
         select *from HMPatient where HMPatient.RecId == HMPatientInsurance.HMPatient;

        throw error("Class/Plan is assigned to patient (MRN ::" +HMPatient.getMRN()+" )" );
    }
    else
    {
    super();
    }
}

public static HMInsuranceContractClassOrPlans CopyPlan(HMInsuranceContractClassOrPlans _plan)
{
    HMInsuranceContractClassOrPlans copyPlan;

    copyPlan.HMContractClassId                  = _plan.HMContractClassId;
    copyPlan.HMContractClassInstructions        = _plan.HMContractClassInstructions;
    copyPlan.HMContractClassName                = _plan.HMContractClassName;
    copyPlan.HMContractClassNetwork             = _plan.HMContractClassNetwork;
    copyPlan.HMContractClassStatus              = _plan.HMContractClassStatus;
    copyPlan.HMInsuranceContract                = _plan.HMInsuranceContract;
    copyPlan.HMMaxEmergencyDeductibleLimit      = _plan.HMMaxEmergencyDeductibleLimit;
    copyPlan.HMMaxInPatientDeductibleLimit      = _plan.HMMaxInPatientDeductibleLimit;
    copyPlan.HMMaxOutPatientDeductibleLimit     = _plan.HMMaxOutPatientDeductibleLimit;
    copyPlan.HMMaxPreAuthorizationLimitAmount   = _plan.HMMaxPreAuthorizationLimitAmount;
    copyPlan.PriceDiscAdmTable                  = _plan.PriceDiscAdmTable;

    return copyPlan;
}

public static HMInsuranceContractClassOrPlans find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMInsuranceContractClassOrPlans  hmInsuranceContractClassOrPlans;

    hmInsuranceContractClassOrPlans.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmInsuranceContractClassOrPlans.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmInsuranceContractClassOrPlans
        where hmInsuranceContractClassOrPlans.RecId == _recid;

    return hmInsuranceContractClassOrPlans;
}

