/// <summary>
///    Determines whether the specified record is located in the <c>HMInventAlternativeItem</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMInventAlternativeItem</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMInventAlternativeItem</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(HMCode20 _networkCode)
{
    boolean ret = true;

    if (!HMInsuranceNetwork::exist(_networkCode))
    {
        ret = checkFailed(strfmt(HMInsuranceNetwork::txtNotExist(), _networkCode));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMInsuranceNetwork</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMInsuranceNetwork</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(HMCode20 _hmInsuranceNetworkCode)
{
    boolean found;

    found = (select firstonly
             NetworkId
         from
             HMInsuranceNetwork
         where
             HMInsuranceNetwork.NetworkId == _hmInsuranceNetworkCode).REcId != 0;

    return found;
}

/// <summary>
///    Finds the specified record in the <c>HMInsuranceNetwork</c> table.
/// </summary>
/// <param name="HMInsuranceNetwork">
///    The record ID of the <c>HMInsuranceNetwork</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMInsuranceNetwork</c> table; otherwise, an empty record.
/// </returns>
public static HMInsuranceNetwork find(HMCode20               _hmInsuranceNetwork,
                                        boolean             _forUpdate = false,
                                        ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMInsuranceNetwork   HMInsuranceNetwork;

    HMInsuranceNetwork.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        HMInsuranceNetwork.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from HMInsuranceNetwork
    where HMInsuranceNetwork.NetworkId == _hmInsuranceNetwork;

    return HMInsuranceNetwork;
}

/// <summary>
///    Gets the message to use when a specific record in the <c>HMInsuranceNetwork</c> does not exist.
/// </summary>
/// <returns>
///    The message that states a record does not exist.
/// </returns>
public static TxtNotExist txtNotExist()
{
    return "@SYS127989";
}
