/// /// <summary>
/// Displays in a sentance Drug dose, route and frequency from <c>HMMedDictionary</c>.
/// </summary>
/// <returns>
/// sig from <c>HMMedDictionary</c>table.
/// </returns>
//BP Deviation Documented
[SysClientCacheDataMethodAttribute(true)]
display HMShortDescription showMedicationSig()
{
    HMShortDescription                 sig;

    sig=strfmt('%1',this.HMDoseQuantityDefault) + ' ' + HMMedDosageUnit::find(this.HMMedDosageUnitDefault).Description + '  '+ HMMedicationRoute::find(this.HMMedicationRouteDefault).Description + ' ' + HMMedUsageFrequency::find(this.HMMedUsageFrequencyDefault).Description;

    return sig;
}

/// <summary>
///    Determines whether the specified record is located in the <c>HMInventTable</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMInventTable</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMInventTable</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMInventTable::exist(_recId))
    {
        ret = checkFailed(strfmt(HMInventTable::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMInventTable</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMInventTable</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 HMInventTable
             where
                 HMInventTable.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///    Finds the specified record in the <c>HMInventTable</c> table.
/// </summary>
/// <param name="_recid">
///    The record ID of the <c>HMInventTable</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMInventTable</c> table; otherwise, an empty record.
/// </returns>
public static HMInventTable find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMInventTable   hmInventTable;

    hmInventTable.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmInventTable.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmInventTable
        where hmInventTable.RecId == _recid;

    return hmInventTable;
}

/// <summary>
///    Finds the specified record in the <c>HMInventTable</c> table.
/// </summary>
/// <param name="_itemId">
///    The inventtable of the <c>HMInventTable</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMInventTable</c> table; otherwise, an empty record.
/// </returns>
public static HMInventTable findByItemId(
ItemId               _itemId,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMInventTable   hmInventTable;

    hmInventTable.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmInventTable.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmInventTable
        where hmInventTable.InventTable == _itemId;

    return hmInventTable;
}

/// <summary>
///    Gets the message to use when a specific record in the <c>HMInventTable</c> does not exist.
/// </summary>
/// <returns>
///    The message that states a record does not exist.
/// </returns>
public static TxtNotExist txtNotExist()
{
    return "@SYS127989";
}

/// <summary>
///
/// </summary>
/// <returns></returns>
public boolean validateWrite()
{
    HMInventTable   hMInventTablelocal;
    boolean ret;
    str txt;

    ret = super();

    if(ret==true)
    {
        select firstOnly RecId,InventTable from hMInventTablelocal
    where hMInventTablelocal.PPSCode==this.PPSCode && hMInventTablelocal.PPSCode!=0;

        if(hMInventTablelocal)
        {
            txt=strFmt("PPSCode already assign with service code %1",hMInventTablelocal.InventTable);
            ret=checkFailed(txt);
        }
    }
    return ret;
}
