/// <summary>
///    Validates that the specified record exists in the <c>HMOPApointmentsHistoryTmp</c> table; if
///    not, an error message is displayed.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMOPApointmentsHistoryTmp</c> record to check for existence.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean checkExist(RecId _recId)
{
    boolean isExisting = true;

    if (!HMOPApointmentsHistoryTmp::exist(_recId))
    {
        isExisting = checkFailed(strfmt(HMOPApointmentsHistoryTmp::txtNotExist(),_recId));
    }

    return isExisting;
}

/// <summary>
///    Indicates whether the specified record in the <c>HMOPApointmentsHistoryTmp</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMOPApointmentsHistoryTmp</c> record to check for existence.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    HMOPApointmentsHistoryTmp     hmOPApointmentsHistoryTmp;

    select firstonly
        RecId
    from
        hmOPApointmentsHistoryTmp
    where
        hmOPApointmentsHistoryTmp.RecId == _recId;

    return hmOPApointmentsHistoryTmp != null;
}

/// <summary>
///    Finds the specified record in the <c>HMOPApointmentsHistoryTmp</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMOPApointmentsHistoryTmp</c> record to find.
/// </param>
/// <param name="_forupdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMOPApointmentsHistoryTmp</c> table; otherwise, an empty record.
/// </returns>
public static HMOPApointmentsHistoryTmp find(
    RecId _recId,
    boolean _forupdate = false,
    ConcurrencyModel _concurrencyModel = ConcurrencyModel::Auto)
{
    HMOPApointmentsHistoryTmp   hmOPApointmentsHistoryTmp;

    hmOPApointmentsHistoryTmp.selectForUpdate(_forupdate);
    if (_forupdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmOPApointmentsHistoryTmp.concurrencyModel(_concurrencyModel);
    }

    select firstonly
            hmOPApointmentsHistoryTmp
        where
            hmOPApointmentsHistoryTmp.RecId == _recId;

    return hmOPApointmentsHistoryTmp;
}

