public void initValue()
{
    super();
    this.IsDisplayLongAges = NoYes::Yes;
    this.ToUnit = HMPatinetAgeUnit::Day;
    this.FromUnit = HMPatinetAgeUnit::Day;
    this.FromValue = 1;
    this.ToValue = 30;
    this.MaximumDiagnosisInPatientEncounter = 5;
    this.AutoResolveAcuteActiveProblemDays = 30;
    this.DefaultLevelOfChronicity = HMChronicity::Acute;
    this.MaximumDiagnosisInInsuranceClaim = 4;
    this.HMClinicalMeasurementTimePeriod = HMClinicalMeasurementTimePeriods::OneMonth;
    this.DefaultAuthorizatioPeriodDays = 30;
    this.PatientReferralMaximumDiagnosis = 4;
    this.DefaultInsurance = NoYes::Yes;
}

public void update()
{
    super();
    flush HMParameters;
}

boolean validateDelete()
{
    return false;
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    // Selected Item Model Group must be defined as non stocked
    //if (ret && fieldId2name(tableNum(HMParameters), _fieldIdToCheck) == 'DefaultInventModelGroupId')
    //{
        //if (InventModelGroup::find(this.DefaultInventModelGroupId).isStocked())
        //{
            //info('Please select a non stocked item model group');
            //ret = false;
        //}
    //}

    // Maximum number of diagnosis in a patient case
    if (ret && fieldId2name(tableNum(HMParameters), _fieldIdToCheck) == 'MaximumDiagnosisInPatientEncounter')
    {
        if ( this.MaximumDiagnosisInPatientEncounter < 1)
        {
           ret = ret && checkFailed("@MAZ4402");
        }
    }

    // Auto resolve acute active problems
    if (ret && fieldId2name(tableNum(HMParameters), _fieldIdToCheck) == 'AutoResolveAcuteActiveProblemDays')
    {
        if( this.AutoResolveAcuteActiveProblemDays < 1 || this.AutoResolveAcuteActiveProblemDays > 365 )
        {
            ret = ret && checkFailed("@MAZ1072");
        }
    }

    // From unit field
    if (ret && fieldId2name(tableNum(HMParameters), _fieldIdToCheck) == 'FromUnit')
    {
        switch(this.FromUnit)
        {
            case HMPatinetAgeUnit::Day:
                if (this.FromValue < 1 || this.FromValue > 30)
                {
                    ret = ret && checkFailed(strfmt("@MAZ1296", 1, 30));
                }

                if (this.ToUnit == HMPatinetAgeUnit::Day)
                {
                    if (this.FromValue == this.ToValue)
                    {
                        ret = ret && checkFailed("@MAZ1307");
                    }

                    if (this.FromValue>this.ToValue)
                    {
                        ret = ret && checkFailed("@MAZ1306");
                    }
                }
                break;

            case HMPatinetAgeUnit::Month:
                if (this.FromValue < 1 || this.FromValue > 12)
                {
                    ret = ret && checkFailed(strfmt("@MAZ1299", 1, 12));
                }

                if (this.ToUnit == HMPatinetAgeUnit::Month)
                {
                    if (this.FromValue == this.ToValue)
                    {
                        ret = ret && checkFailed("@MAZ1305");
                    }
                    if (this.FromValue>this.ToValue)
                    {
                        ret = ret && checkFailed("@MAZ1304");
                    }
                }
                break;

            case HMPatinetAgeUnit::Year:
                if (this.FromValue < 1 || this.FromValue > 100)
                {
                    ret = ret && checkFailed(strfmt("@MAZ1300", 1, 100));
                }
                if (this.ToUnit == HMPatinetAgeUnit::Year)
                {
                    if (this.FromValue == this.ToValue)
                    {
                        ret = ret && checkFailed("@MAZ1303");
                    }
                    if (this.FromValue>this.ToValue)
                    {
                        ret = ret && checkFailed("@MAZ1302");
                    }
                }
                break;
            }
        }

        // To unit field
        if (ret && fieldId2name(tableNum(HMParameters), _fieldIdToCheck) == 'ToUnit')
        {
            switch (this.ToUnit)
            {
                case HMPatinetAgeUnit::Day:
                    if (this.ToValue < 1 || this.ToValue > 30)
                    {
                        ret = ret && checkFailed(strfmt("@MAZ1296", 1, 30));
                    }
                    break;

                case HMPatinetAgeUnit::Month:
                    if (this.ToValue < 1 || this.ToValue > 12)
                    {
                        ret = ret && checkFailed(strfmt("@MAZ1299", 1, 12));
                    }
                    break;

                case HMPatinetAgeUnit::Year:
                    if (this.ToValue < 1 || this.ToValue > 100)
                    {
                        ret = ret && checkFailed(strfmt("@MAZ1300", 1, 100));
                    }
                    break;
            }
        }

        // ToUnit & FromUnit field
        if (ret
            && (fieldId2name(tableNum(HMParameters), _fieldIdToCheck) == 'ToUnit'
            || fieldId2name(tableNum(HMParameters), _fieldIdToCheck) == 'ToUnit'))
        {
            if (this.FromUnit > this.ToUnit)
            {
                ret = ret && checkFailed("@MAZ1301");
            }
        }

    // Maximum diagnosis in a referral
    if (ret && fieldId2name(tableNum(HMParameters), _fieldIdToCheck) == 'PatientReferralMaximumDiagnosis')
    {
        if(this.PatientReferralMaximumDiagnosis < 1)
        {
            ret = ret && checkFailed("@MAZ6243");
        }
    }

    return ret;
}

public static server HMItemId defaultCoPayServiceItem()
{
    HMParameters        hmParameter;

    select firstonly DefaultCOPayService from hmParameter;

    if (hmParameter.DefaultCOPayService)
    {
        return hmParameter.DefaultCOPayService;
