static boolean exist()
{
    return (select firstonly RecId from hmParameters).RecId != 0;
}

static HMParameters find(boolean _forupdate=false)
{
    HMParameters parameter;

    parameter.selectForUpdate(_forupdate);

    select firstonly parameter  index Key  where parameter.Key == 0;

    if (!parameter && !parameter.isTmp())
    {
        Company::createParameter(parameter);
    }

    return parameter;
}

public static DocuTypeId getCCDDocumentDocuTypeId()
{
    DocuTypeId typeId;
    DocuType docuType;
    HMParametersDocumentAttachmentType      hmParametersDocumentAttachmentTypeLocal;

    if (HMParameters::find().IsDocTypeSameForAllAttachments == NoYes::Yes)
    {
        select firstOnly * from docuType
            where docuType.RecId == HMParameters::find().DefaultDocType;

        typeId = docuType.TypeId;
    }
    else
    {

        select firstOnly * from hmParametersDocumentAttachmentTypeLocal
            join * from docuType
            where hmParametersDocumentAttachmentTypeLocal.HMAttachmentDocType == HMAttachmentDocType::CCD
            && hmParametersDocumentAttachmentTypeLocal.DocuType == docuType.RecId;

        typeId = docuType.TypeId;
    }
    return typeId;
}

