static client server DocuTypeId getDefaultDocuTypeId()
{
    HMParameters        hmParameter;
    DocuType            docuType;

    select firstOnly *
        from hmParameter
        join * from docuType where hmParameter.DefaultDocType == docuType.RecId;

    return docuType.TypeId;
}

