public static Num getNewNumSeqForWaitingListOrder(RecId _operatingUnit)
{
    SysLastValue                    nextNumSeq;
    Num                             number;
    NumberSequenceReference         waitListOrderNumberSeqReference;
    NumberSequenceTable             waitListOrderNumberSeqTable;
    NumberSeq                       waitListOrderNumberSeq;

    waitListOrderNumberSeqReference = HMParameters::numRefWaitNumberId(_operatingUnit);
    waitListOrderNumberSeqTable     = NumberSequenceTable::find(waitListOrderNumberSeqReference.NumberSequenceId);

    if (waitListOrderNumberSeqTable)
    {
        // generate waitlist order number if number sequence code is created.
        waitListOrderNumberSeq = NumberSeq::newGetNumFromId(waitListOrderNumberSeqTable.RecId, true, true, nextNumSeq, true);

        if (waitListOrderNumberSeq)
        {
            [number] = nextNumSeq.value;
        }
    }

    return number;
}

