public static DataAreaId getPharmacyDataArea()
{
    HMParameters parameters;
    CompanyInfo companyInfo;
    parameters = Hmparameters::find();

    select DataArea from companyInfo
    where companyInfo.RecId == parameters.CompanyInfo_Pharmacy;

    return companyInfo.DataArea;
}

