/// <summary>
///    Determines whether the specified record exists in the <c>HMParametersDocumentAttachmentType</c>v table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMParametersDocumentAttachmentType</c> table record to check for existence.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMParametersDocumentAttachmentType</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMParametersDocumentAttachmentType::exist(_recId))
    {
        ret = checkFailed(strfmt(HMParametersDocumentAttachmentType::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMParametersDocumentAttachmentType</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMParametersDocumentAttachmentType</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 hmParametersDocumentAttachmentType
             where
                 hmParametersDocumentAttachmentType.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///    Finds the specified record in the <c>HMParametersDocumentAttachmentType</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMParametersDocumentAttachmentType</c> record to find.
/// </param>
/// <param name="_forupdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record int the <c>HMParametersDocumentAttachmentType</c> table; otherwise, an empty record.
/// </returns>
public static HMParametersDocumentAttachmentType find(
    RecId                   _recId,
    boolean                 _forupdate = false,
    ConcurrencyModel        _concurrencyModel = ConcurrencyModel::Auto)
{
    HMParametersDocumentAttachmentType hmParametersDocumentAttachmentType;

    hmParametersDocumentAttachmentType.selectForUpdate(_forupdate);
    if (_forupdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmParametersDocumentAttachmentType.concurrencyModel(_concurrencyModel);
    }

    select firstonly hmParametersDocumentAttachmentType
        where hmParametersDocumentAttachmentType.RecId == _recId;

    return hmParametersDocumentAttachmentType;
}

