/// <summary>
/// Validates that the specified record exists in the <c>HMPatientServicePerformedLog</c> table; if not, an error message is displayed.
/// </summary>
/// <param name="_recId">
/// The unique ID of the record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean checkExist(recId _recId)
{
    boolean ret = true;

    if (!HMPatientServicePerformedLog::exist(_recId))
    {
        ret = checkFailed(strfmt(HMPatientServicePerformedLog::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
/// Indicates whether the specified record in the <c>HMPatientServicePerformedLog</c> table exists.
/// </summary>
/// <param name="_recId">
/// The unique ID of the record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(recId _recId)
{
    boolean existHMPatientServicePerformedLog;

    existHMPatientServicePerformedLog = (select firstonly
                RecId
            from
                HMPatientServicePerformedLog
            where
                HMPatientServicePerformedLog.RecId == _recId).RecId != 0;

    return existHMPatientServicePerformedLog;
}

/// <summary>
/// Finds the specified record in the <c>HMPatientServicePerformedLog</c> table.
/// </summary>
/// <param name="_recId">
/// The unique ID of the record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional
/// </param>
/// <returns>
/// A record in the <c>HMPatientServicePerformedLog</c> table; otherwise, an empty record.
/// </returns>
public static HMPatientServicePerformedLog find(
    recId   _recId,
    boolean _forUpdate = false)
{
    HMPatientServicePerformedLog hmPatientServicePerformedLog;
    if (_recId)
    {
        hmPatientServicePerformedLog.selectForUpdate(_forUpdate);

        select firstonly hmPatientServicePerformedLog
            where hmPatientServicePerformedLog.RecId == _recId;
    }
    return hmPatientServicePerformedLog;
}
