/// <summary>
///    Determines whether the specified record is located in the <c>HMProcedureCodeMaster</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMProcedureCodeMaster</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMProcedureCodeMaster</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMProcedureCodeMaster::exist(_recId))
    {
        ret = checkFailed(strfmt(HMProcedureCodeMaster::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMProcedureCodeMaster</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMProcedureCodeMaster</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 HMProcedureCodeMaster
             where
                 HMProcedureCodeMaster.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///    Finds the specified record in the <c>HMProcedureCodeMaster</c> table.
/// </summary>
/// <param name="_recid">
///    The record ID of the <c>HMProcedureCodeMaster</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMProcedureCodeMaster</c> table; otherwise, an empty record.
/// </returns>
public static HMProcedureCodeMaster find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMProcedureCodeMaster   hmProcedureCodeMaster;

    hmProcedureCodeMaster.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmProcedureCodeMaster.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmProcedureCodeMaster
        where hmProcedureCodeMaster.RecId == _recid;

    return hmProcedureCodeMaster;
}

/// <summary>
///     Finds the specified record in the <c>HMProcedureCode</c> table.
/// </summary>
/// <param name="_code">
///     The record ID of the <c>HMProcedureCode</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMProcedureCode</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMProcedureCodeMaster findbyCode(
Description               _code,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMProcedureCodeMaster  hmProcedureCodeMaster;
    hmProcedureCodeMaster.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmProcedureCodeMaster.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmProcedureCodeMaster where hmProcedureCodeMaster.Code == _code;

    return hmProcedureCodeMaster;
}

/// <summary>
///    Gets the message to use when a specific record in the <c>HMProcedureCodeMaster</c> does not exist.
/// </summary>
/// <returns>
///    The message that states a record does not exist.
/// </returns>
public static TxtNotExist txtNotExist()
{
    return "@SYS127989";
}
