public void delete()
{
    HMResourceSchedulingAppointment hmResourceSchedulingAppointment;
    /*
    hmResourceSchedulingAppointment.skipDeleteActions(true);
    delete_from hmResourceSchedulingAppointment where hmResourceSchedulingAppointment.HMRecurrentApptDetail == this.RecId
    && hmResourceSchedulingAppointment.AppointmentStatus == HMResourceSchedulingAppointmentStatus::Open;
    */
    while select forupdate * from hmResourceSchedulingAppointment where hmResourceSchedulingAppointment.HMRecurrentApptDetail == this.RecId
    && hmResourceSchedulingAppointment.AppointmentStatus == HMResourceSchedulingAppointmentStatus::Open
    {
        hmResourceSchedulingAppointment.skipDeleteActions(true);
        hmResourceSchedulingAppointment.delete();
    }


    super();
}

[SysClientCacheDataMethodAttribute(true)]
public display Description getPatientChartName()
{
    HMResourceSchedulingAppointment HMResourceSchedulingAppointment;
    Description MRN;

    select * from HMResourceSchedulingAppointment where HMResourceSchedulingAppointment.HMRecurrentApptDetail == this.RecId;
    MRN = HMResourceSchedulingAppointment.getPatientChartNumByPatientRecId(HMResourceSchedulingAppointment.HMPatient);

    return MRN;
}

[SysClientCacheDataMethodAttribute(true)]
public display Description getPatientName()
{
    HMResourceSchedulingAppointment HMResourceSchedulingAppointment;
    Description patientName;

    select * from HMResourceSchedulingAppointment where HMResourceSchedulingAppointment.HMRecurrentApptDetail == this.RecId;
    patientName = HMResourceSchedulingAppointment.getPatientNamebyPatientRecId(HMResourceSchedulingAppointment.HMPatient);

    return patientName;
}

[SysClientCacheDataMethodAttribute(true)]
display HMMemo getRecurrentDesc()
{
    HMMemo recurrentDesc;

    //daily
    if (this.RecurrenceType == HMCalenderName::Daily)
    {
        if(this.EveryNDays == HMEveryNDays::EveryDays)
        {
            if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndByDate)
            {
               recurrentDesc = 'Occurs every ' + int2str(this.NoOfDays) + ' days effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.EndByDate,0);
            }
            else if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndAfterDate)
            {
                recurrentDesc = 'Occurs every ' + int2str(this.NoOfDays) + ' days effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.OccuranceEndDate,0);
            }
        }
        else if(this.EveryNDays == HMEveryNDays::EveryWeekDay)
        {
            if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndByDate)
            {
               recurrentDesc = 'Occurs every weekdays effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.EndByDate,0);
            }
            else if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndAfterDate)
            {
               recurrentDesc = 'Occurs every weekdays effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.OccuranceEndDate,0);
            }
        }
    }


    //weekly
    else if (this.RecurrenceType == HMCalenderName::Weekly)
    {
        if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndByDate)
        {
            recurrentDesc = 'Occurs every ' + int2str(this.NoOfWeeks) + ' weeks on ' + this.weekDaysNames() + ' effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.EndByDate,0);
        }
        else if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndAfterDate)
        {
           recurrentDesc = 'Occurs every ' + int2str(this.NoOfWeeks) + ' weeks on ' + this.weekDaysNames()  + ' effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.OccuranceEndDate,0);
        }

    }


    //monthly
    else if (this.RecurrenceType == HMCalenderName::Monthly)
    {
        if(this.MonthTypeOption == HMMonthTypeOption::Day)
        {
            if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndByDate)
            {
               recurrentDesc = 'Occurs day ' + int2str(this.DayNumber) + ' of every ' + int2str(this.MonthNumber) + ' months' + ' effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.EndByDate,0);
            }
            else if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndAfterDate)
            {
               recurrentDesc = 'Occurs day ' + int2str(this.DayNumber) + ' of every ' + int2str(this.MonthNumber) + ' months' + ' effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.OccuranceEndDate,0);
            }
        }
        else if(this.MonthTypeOption == HMMonthTypeOption::The)
        {
            if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndByDate)
            {
               recurrentDesc = 'Occurs the ' + enum2str(this.DayNumberEnum) + ' ' + enum2str(this.DayName)  + ' of every ' + int2str(this.EveryNMonth) + ' months' + ' effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.EndByDate,0);
            }
            else if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndAfterDate)
            {
               recurrentDesc = 'Occurs the ' + enum2str(this.DayNumberEnum) + ' ' + enum2str(this.DayName)  + ' of every ' + int2str(this.EveryNMonth) + ' months' + ' effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.OccuranceEndDate,0);
            }
        }
    }

    //yearly
    else if (this.RecurrenceType == HMCalenderName::Yearly)
    {
        if(this.YearTypeOption == HMYearTypeOption::OnYearly)
        {
            if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndByDate)
            {
               recurrentDesc = 'Occurs every ' + int2str(this.RecurEveryNYear) + ' years on ' + int2str(this.DayOfYear ) + ' '+ enum2str(this.EveryMonthOfYear) + ' effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.EndByDate,0);
            }
            else if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndAfterDate)
            {
               recurrentDesc = 'Occurs every ' + int2str(this.RecurEveryNYear) + ' years on ' + int2str(this.DayOfYear ) + ' ' +enum2str(this.EveryMonthOfYear) + ' effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.OccuranceEndDate,0);
            }
        }
        else if(this.YearTypeOption == HMYearTypeOption::OntheYearly)
        {
            if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndByDate)
            {
               recurrentDesc = 'Occurs every ' + int2str(this.RecurEveryNYear) + ' years on ' + enum2str(this.WeekNumberEnumForYear) + ' ' + enum2str(this.WeekDayNameForYear) + ' of ' + enum2str(this.MonthNameForYear) + ' effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.EndByDate,0);
            }
            else if(this.RecurrenceRangeType == HMRecurrenceRangeType::EndAfterDate)
            {
               recurrentDesc = 'Occurs every ' + int2str(this.RecurEveryNYear) + ' years on ' + enum2str(this.WeekNumberEnumForYear) + ' ' + enum2str(this.WeekDayNameForYear) + ' of ' + enum2str(this.MonthNameForYear) + ' effective ' + date2StrUsr(this.StartDate,0) + ' until ' + date2StrUsr(this.OccuranceEndDate,0);
            }
        }
    }


    return  recurrentDesc;
}

private Description weekDaysNames()
{
    Description weekDaysName;

    if(this.Sunday == NoYes::Yes)
    {
        weekDaysName = weekDaysName + ' sunday ';
    }
    if(this.Monday == NoYes::Yes)
    {
        weekDaysName = weekDaysName + ' monday ';
    }
    if(this.Tuesday == NoYes::Yes)
    {
        weekDaysName = weekDaysName + ' tuesday ';
    }
    if(this.Wednesday == NoYes::Yes)
    {
        weekDaysName = weekDaysName + ' wednesday ';
    }
    if(this.Thursday == NoYes::Yes)
    {
        weekDaysName = weekDaysName + ' thursday ';
    }
    if(this.Friday == NoYes::Yes)
    {
        weekDaysName = weekDaysName + ' friday ';
    }
    if(this.Saturday == NoYes::Yes)
    {
        weekDaysName = weekDaysName + ' saturday ';
    }

    return weekDaysName;
}
