/// <summary>
///     Finds the specified record in the <c>HMResourceMasterScheduleType</c> table.
/// </summary>
/// <param name="_recid">
///     The record ID of the <c>HMResourceMasterScheduleType</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMResourceMasterScheduleType</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMResourceMasterScheduleType find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMResourceMasterScheduleType  hmResourceMasterScheduleType;

    hmResourceMasterScheduleType.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmResourceMasterScheduleType.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmResourceMasterScheduleType where hmResourceMasterScheduleType.RecId == _recid;

    return hmResourceMasterScheduleType;
}

/// <summary>
///     Finds the specified record in the <c>HMResourceMasterScheduleType</c> table.
/// </summary>
/// <param name="_recid">
///     The record ID of the <c>HMResourceMasterScheduleType</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMResourceMasterScheduleType</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMResourceMasterScheduleType findbyResourceMasterandTypeCode(
RecId               _resourceMasterRecid,
RecId               _typeCodeRecid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMResourceMasterScheduleType  hmResourceMasterScheduleType;

    hmResourceMasterScheduleType.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmResourceMasterScheduleType.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmResourceMasterScheduleType
        where hmResourceMasterScheduleType.HMResourceMaster == _resourceMasterRecid
            && hmResourceMasterScheduleType.HMResourceSchedulingTypeCode == _typeCodeRecid;

    return hmResourceMasterScheduleType;
}

public static real findDurationResourceAndType(
RecId               _resourceMasterRecid,
RecId               _typeCodeRecid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMResourceMasterScheduleType  hmResourceMasterScheduleType;
    real                          duration;

    hmResourceMasterScheduleType.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmResourceMasterScheduleType.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmResourceMasterScheduleType
        where hmResourceMasterScheduleType.HMResourceMaster == _resourceMasterRecid
            && hmResourceMasterScheduleType.HMResourceSchedulingTypeCode == _typeCodeRecid;

    if (hmResourceMasterScheduleType.DurationUnit == HMMinHrs::Hours)
    {
        duration = hmResourceMasterScheduleType.Duration * 60;
    }
    else
    {
        duration = hmResourceMasterScheduleType.Duration;
    }

    return duration;
}
