[SysClientCacheDataMethodAttribute(true)]
public display AmountCur salesAmount()
{
    salesTotals salesTotals;
    SalesTable salesTableLocal;
    salesTableLocal = SalesTable::find(this.SalesTable);
    if(salesTableLocal.SalesStatus == SalesStatus::Backorder)
    {
        salesTotals = SalesTotals::construct(salesTableLocal);
        return salesTotals.totalAmount();
    }
    else
    {
        return salesTableLocal.amountInvoiced();
    }
}

/// <summary>
///    Determines whether the specified record is located in the <c>HMResourceSchedulingAppointmentSalesTbl</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMResourceSchedulingAppointmentSalesTbl</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMResourceSchedulingAppointmentSalesTbl</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMResourceSchedulingAppointmentSalesTbl::exist(_recId))
    {
        ret = checkFailed(strfmt(HMResourceSchedulingAppointmentSalesTbl::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMResourceSchedulingAppointmentSalesTbl</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMResourceSchedulingAppointmentSalesTbl</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 HMResourceSchedulingAppointmentSalesTbl
             where
                 HMResourceSchedulingAppointmentSalesTbl.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///     Finds the specified record in the <c>HMResourceSchedulingAppointmentSalesTbl</c> table.
/// </summary>
/// <param name="_recid">
///     The record ID of the <c>HMResourceSchedulingAppointmentSalesTbl</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMResourceSchedulingAppointmentSalesTbl</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMResourceSchedulingAppointmentSalesTbl find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMResourceSchedulingAppointmentSalesTbl  HMResourceSchedulingAppointmentSalesTbl;

    HMResourceSchedulingAppointmentSalesTbl.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        HMResourceSchedulingAppointmentSalesTbl.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from HMResourceSchedulingAppointmentSalesTbl where HMResourceSchedulingAppointmentSalesTbl.RecId == _recid;

    return HMResourceSchedulingAppointmentSalesTbl;
}

[SysClientCacheDataMethodAttribute(true)]
public static real getInvoiceAmount(HMResourceSchedulingAppointmentSalesTbl _hmResourceSchedulingAppointmentSalesTbl)
{
    SalesTotals salesTotals;
    SalesTable  salesTable;
    container   displayFields;
    str         amountInclTax;
    CustInvoiceJour custInvoiceJour;
    if(_hmResourceSchedulingAppointmentSalesTbl.Invoiced == NoYes::Yes)
    {
        select  firstOnly InvoiceAmount from custInvoiceJour
        where custInvoiceJour.salesid == _hmResourceSchedulingAppointmentSalesTbl.SalesTable;

        return custInvoiceJour.InvoiceAmount;
    }
    salesTable = salesTable::find(_hmResourceSchedulingAppointmentSalesTbl.SalesTable);
    salesTotals  =  SalesTotals::construct(salesTable, salesUpdate::All);
    salesTotals.calc();
    displayFields =  salesTotals.displayFieldsCurrency(salesTotals.currencyCode());

    amountInclTax   = conpeek(displayFields, TradeTotals::posTotalAmount());

    return str2num(amountInclTax);
}

