public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if(ret)
    {

        if(this.DayStartTime > this.DayEndTime)
        {
            warning("@MAZ1691");
            ret = false;
        }
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record is located in the <c>HMResourceSchedulingDayPlan</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMResourceSchedulingDayPlan</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMResourceSchedulingDayPlan</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMResourceSchedulingDayPlan::exist(_recId))
    {
        ret = checkFailed(strfmt(HMResourceSchedulingDayPlan::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMResourceSchedulingDayPlan</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMResourceSchedulingDayPlan</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 hmResourceSchedulingDayPlan
             where
                 hmResourceSchedulingDayPlan.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///     Finds the specified record in the <c>HMResourceSchedulingDayPlan</c> table.
/// </summary>
/// <param name="_recid">
///     The record ID of the <c>HMResourceSchedulingDayPlan</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMResourceSchedulingDayPlan</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMResourceSchedulingDayPlan find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMResourceSchedulingDayPlan  hmResourceSchedulingDayPlan;

    hmResourceSchedulingDayPlan.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmResourceSchedulingDayPlan.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmResourceSchedulingDayPlan where hmResourceSchedulingDayPlan.RecId == _recid;

    return hmResourceSchedulingDayPlan;
}

