Method.Source:
edit String10 arrivalTime(boolean _set, String10 _value)
{
    String10 retVal;

    if(_set)
    {
        this.ArrivalTime = _value == '' ? -1 : str2time(_value);
    }

    retVal = this.ArrivalTime == -1 ? '' : strDel(time2Str(this.ArrivalTime,TimeSeparator::Colon,TimeFormat::AMPM),6,3);

    return retVal;
}

public boolean validateWrite()
{
    boolean ret;
    HMCaseType caseType;
    HMResourceSchedulingDayPlanItem     hmResourceSchedulingDayPlanItemLocal;

    ret = super();

    if(ret)
    {
        caseType = HMResourceSchedulingPlanningObject::find(HMResourceSchedulingDayPlan::find(this.HMResourceSchedulingDayPlan).HMResourceSchedulingPlanningObject).CaseType;

        if(caseType == HMCaseType::OutPatient)
        {
            if(this.StartTime > this.EndTime)
            {
                warning("@MAZ1691");
                ret = false;
            }
            /*
            if (this.NumberOfTimeSlots < 1 || this.NumberOfTimeSlots > 99)
            {
                warning(strFmt("@MAZ1692",1,99));
                ret = false;
            }
            */

            if(ret)
            {
               /* select count(RecId) from hmResourceSchedulingDayPlanItemLocal
                    where hmResourceSchedulingDayPlanItemLocal.RecId != this.RecId
                    && hmResourceSchedulingDayPlanItemLocal.StartTime < this.StartTime
                    && hmResourceSchedulingDayPlanItemLocal.EndTime > this.EndTime
                    && hmResourceSchedulingDayPlanItemLocal.HMSpecialtyCode == this.HMSpecialtyCode
                    && hmResourceSchedulingDayPlanItemLocal.HMResourceSchedulingDayPlan == this.HMResourceSchedulingDayPlan
                    && hmResourceSchedulingDayPlanItemLocal.HMResourceSchedulingTypeCode == this.HMResourceSchedulingTypeCode;
               */
               select count(RecId) from hmResourceSchedulingDayPlanItemLocal
                    where hmResourceSchedulingDayPlanItemLocal.RecId != this.RecId
                    && ((hmResourceSchedulingDayPlanItemLocal.StartTime < this.StartTime && hmResourceSchedulingDayPlanItemLocal.EndTime > this.StartTime)
                    || (hmResourceSchedulingDayPlanItemLocal.StartTime < this.EndTime && hmResourceSchedulingDayPlanItemLocal.EndTime > this.EndTime))
                    && hmResourceSchedulingDayPlanItemLocal.HMSpecialtyCode == this.HMSpecialtyCode
                    && hmResourceSchedulingDayPlanItemLocal.HMResourceSchedulingDayPlan == this.HMResourceSchedulingDayPlan
                    && hmResourceSchedulingDayPlanItemLocal.HMResourceSchedulingTypeCode == this.HMResourceSchedulingTypeCode;

                if(hmResourceSchedulingDayPlanItemLocal.RecId > 0)
                {
                    warning(strFmt("@MAZ8355",strDel(time2str(this.StartTime,TimeSeparator::Colon,TimeFormat::AMPM),6,3),strDel(time2str(this.EndTime,TimeSeparator::Colon,TimeFormat::AMPM),6,3),HMResourceSchedulingTypeCode::find(this.HMResourceSchedulingTypeCode).Description,HMSpecialtyCode::find(this.HMSpecialtyCode).Description));
                    ret = false;
                }
            }

        }
        else if(caseType == HMCaseType::InPatient)
        {
            if (this.AdmissionQuota < 1 || this.AdmissionQuota > 999)
            {
                warning(strFmt("@MAZ2451",1,999));
                ret = false;
            }

            if(ret)
            {
                select count(RecId) from hmResourceSchedulingDayPlanItemLocal
                    where hmResourceSchedulingDayPlanItemLocal.RecId != this.RecId
                    && hmResourceSchedulingDayPlanItemLocal.HMResourceSchedulingDayPlan == this.HMResourceSchedulingDayPlan
                    && hmResourceSchedulingDayPlanItemLocal.HMResourceSchedulingTypeCode == this.HMResourceSchedulingTypeCode;

                if(hmResourceSchedulingDayPlanItemLocal.RecId > 0)
                {
                    warning(strFmt("@MAZ3240",HMResourceSchedulingTypeCode::find(this.HMResourceSchedulingTypeCode).Description));
                    ret = false;
                }
            }
        }
    }

    return ret;
}

