/// <summary>
///    Determines whether the specified record in the <c>HMResourceSchedulingDayPlanItem</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMResourceSchedulingDayPlanItem</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 hmResourceSchedulingDayPlanItem
             where
                 hmResourceSchedulingDayPlanItem.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///     Finds the specified record in the <c>HMResourceSchedulingDayPlanItem</c> table.
/// </summary>
/// <param name="_recid">
///     The record ID of the <c>HMResourceSchedulingDayPlanItem</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMResourceSchedulingDayPlanItem</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMResourceSchedulingDayPlanItem find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMResourceSchedulingDayPlanItem  hmResourceSchedulingDayPlanItem;

    hmResourceSchedulingDayPlanItem.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmResourceSchedulingDayPlanItem.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmResourceSchedulingDayPlanItem where hmResourceSchedulingDayPlanItem.RecId == _recid;

    return hmResourceSchedulingDayPlanItem;
}

