public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if(this.Month == MonthsOfYear::None)
    {
         warning("@MAZ1725");
         ret = false;
    }

    if(ret)
    {
        if(this.FromDay > this.ToDay)
        {
            warning("@MAZ3380");
            ret = false;
        }
    }

    if (ret)
    {
        if (this.FromDay < 0 || this.FromDay > 31)
        {
            warning(strfmt("@MAZ1726",1,31));
            ret = false;
        }
        if (this.ToDay < 0 || this.ToDay > 31)
        {
            warning(strfmt("@MAZ1727",1,31));
            ret = false;
        }


    }

    return ret;
}

