/// <remarks>
///    If the specified record is not located in the <c>HMResourceSchedulingPublicHoliday</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMResourceSchedulingPublicHoliday::exist(_recId))
    {
        ret = checkFailed(strfmt(HMResourceSchedulingPublicHoliday::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMResourceSchedulingPublicHoliday</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMResourceSchedulingPublicHoliday</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 hmResourceSchedulingPublicHoliday
             where
                 hmResourceSchedulingPublicHoliday.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///     Finds the specified record in the <c>HMResourceSchedulingPublicHoliday</c> table.
/// </summary>
/// <param name="_recid">
///     The record ID of the <c>HMResourceSchedulingPublicHoliday</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMResourceSchedulingPublicHoliday</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMResourceSchedulingPublicHoliday find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMResourceSchedulingPublicHoliday  hmResourceSchedulingPublicHoliday;
    hmResourceSchedulingPublicHoliday.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmResourceSchedulingPublicHoliday.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmResourceSchedulingPublicHoliday where hmResourceSchedulingPublicHoliday.RecId == _recid;

    return hmResourceSchedulingPublicHoliday;
}

