public void delete()
{
    HMResourceSchedulingWeekPlanToCalender      hmResourceSchedulingWeekPlanToCalenderLocal;
    HMResourceSchedulingWeekPlan                hmResourceSchedulingWeekPlanDelete;
    RecId tempRecid;

    tempRecid = this.HMResourceSchedulingWeekPlan;

    super();

    select count(RecId) from hmResourceSchedulingWeekPlanToCalenderLocal
        where hmResourceSchedulingWeekPlanToCalenderLocal.HMResourceSchedulingWeekPlan == tempRecid;

    if( hmResourceSchedulingWeekPlanToCalenderLocal.RecId == 0)
    {
        ttsBegin;
        hmResourceSchedulingWeekPlanDelete = HMResourceSchedulingWeekPlan::find(tempRecid,true);
        if(hmResourceSchedulingWeekPlanDelete)
        {
            hmResourceSchedulingWeekPlanDelete.IsGenerated=NoYes::No;
            hmResourceSchedulingWeekPlanDelete.update();
        }
        ttsCommit;

    }

}

public boolean validateDelete()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        if(this.YearNo < year(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone())))
        {
            ret = false;
            warning("@MAZ12584");
        }
        else if(this.YearNo == year(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone())) && this.WeekNo < weekOfYear(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone())))
        {
            ret = false;
            warning("@MAZ12584");
        }
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;
    HMResourceSchedulingPlanningObject  planningObject;
    date startDate;

    ret = super();

    planningObject = HMResourceSchedulingPlanningObject::find(HMResourceSchedulingWeekPlan::find(this.HMResourceSchedulingWeekPlan).HMResourceSchedulingPlanningObject);

    if (ret)
    {
        startDate = HMResourceSchedulingHelper::date2Week(this.YearNo, this.WeekNo);

        if(startDate < planningObject.Startdate || (startDate+7) > planningObject.EndDate)
        {
            ret = false;
            error(strFmt("@MAZ13311", this.WeekNo, this.YearNo));
        }
    }

    return ret;
}

