public void initValue()
{
    super();
    this.Active = NoYes::Yes;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        if(this.AgeMin && this.AgeMin > 150)
        {
            ret = false;
            warning(strFmt("@MAZ9012","@MAZ8960",150));
        }

        if(this.AgeMax && this.AgeMax > 150)
        {
            ret = false;
            warning(strFmt("@MAZ9012","@MAZ8961",150));
        }


        if(this.AgeMin && this.AgeMax && this.AgeMax < this.AgeMin)
        {
            ret = false;
            warning("@MAZ8966");
        }
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record is located in the <c>HMSalutation</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMSalutation</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMSalutation</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMSalutation::exist(_recId))
    {
        ret = checkFailed(strfmt(HMSalutation::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMSalutation</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMSalutation</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 hmSalutation
             where
                 hmSalutation.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///    Finds the specified record in the <c>HMSalutation</c> table.
/// </summary>
/// <param name="_recid">
///    The record ID of the <c>HMSalutation</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMSalutation</c> table; otherwise, an empty record.
/// </returns>
public static HMSalutation find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMSalutation   hmSalutation;

    hmSalutation.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmSalutation.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmSalutation
        where hmSalutation.RecId == _recid;

    return hmSalutation;
}

/// <summary>
///    Finds the specified record in the <c>HMSalutation</c> table.
/// </summary>
/// <param name="_salutation">
///    The salutation of the <c>HMSalutation</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMSalutation</c> table; otherwise, an empty record.
/// </returns>
public static HMSalutation findBySalutation(
HRMSalutation               _salutation,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMSalutation   hmSalutation;

    hmSalutation.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmSalutation.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmSalutation
        where hmSalutation.Salutation == _salutation;

    return hmSalutation;
}

