[SysClientCacheDataMethodAttribute(true)]
display public Description showParent()
{
    HMOrganizationalUnit        hmOrganizationalUnit;


    select * from hmOrganizationalUnit
        where hmOrganizationalUnit.RecId == this.HMOrganizationalUnit;

    return HMOrganizationalUnit::find(hmOrganizationalUnit.HMOrganizationalUnit).Description;


}

/// <summary>
///    Determines whether the specified record is located in the <c>HMStaffNursingUnit</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMStaffNursingUnit</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMStaffNursingUnit</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMStaffNursingUnit::exist(_recId))
    {
        ret = checkFailed(strfmt(HMStaffNursingUnit::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMStaffNursingUnit</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMStaffNursingUnit</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 hmStaffNursingUnit
             where
                 hmStaffNursingUnit.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///    Finds the specified record in the <c>HMStaffNursingUnit</c> table.
/// </summary>
/// <param name="_recid">
///    The record ID of the <c>HMStaffNursingUnit</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMStaffNursingUnit</c> table; otherwise, an empty record.
/// </returns>
public static HMStaffNursingUnit find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMStaffNursingUnit   hmStaffNursingUnit;

    hmStaffNursingUnit.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmStaffNursingUnit.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmStaffNursingUnit
        where hmStaffNursingUnit.RecId == _recid;

    return hmStaffNursingUnit;
}

public static HMStaffNursingUnit findByClinicStaff(
RecId               _clinicStaffRecId,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMStaffNursingUnit   hmStaffNursingUnit;

    hmStaffNursingUnit.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmStaffNursingUnit.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmStaffNursingUnit
        where hmStaffNursingUnit.HMClinicStaff == _clinicStaffRecId;

    return hmStaffNursingUnit;
}

public static boolean recordExist(RecId clinicStaffRecId, Recid OURecId)
{
    HMStaffNursingUnit      hmStaffNursingUnit;
    select count(RecId) from hmStaffNursingUnit
    where hmStaffNursingUnit.HMClinicStaff == clinicStaffRecId
    && hmStaffNursingUnit.HMOrganizationalUnit == OURecId;

    if(hmStaffNursingUnit.RecId > 0)
    {
        return true;
    }
    return false;
}

/// <summary>
///    Gets the message to use when a specific record in the <c>HMStaffNursingUnit</c> does not exist.
/// </summary>
/// <returns>
///    The message that states a record does not exist.
/// </returns>
public static TxtNotExist txtNotExist()
{
    return "@SYS127989";
}
