/// <summary>
/// Validates that the specified record exists in the <c>HMTreeSearchingTmp</c> table; if not, an error message is displayed.
/// </summary>
/// <param name="_recId">
/// The unique ID of the record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean checkExist(recId _recId)
{
    boolean ret = true;

    if (!HMTreeSearchingTmp::exist(_recId))
    {
        ret = checkFailed(strfmt(HMTreeSearchingTmp::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
/// Indicates whether the specified record in the <c>HMTreeSearchingTmp</c> table exists.
/// </summary>
/// <param name="_recId">
/// The unique ID of the record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(recId _recId)
{
    boolean existHMTreeSearchingTmp;

    existHMTreeSearchingTmp = (select firstonly
                RecId
            from
                hmTreeSearchingTmp
            where
                hmTreeSearchingTmp.RecId == _recId).RecId != 0;

    return existHMTreeSearchingTmp;
}

/// <summary>
/// Finds the specified record in the <c>HMTreeSearchingTmp</c> table.
/// </summary>
/// <param name="_recId">
/// The unique ID of the record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional
/// </param>
/// <returns>
/// A record in the <c>HMTreeSearchingTmp</c> table; otherwise, an empty record.
/// </returns>
public static HMTreeSearchingTmp find(
    recId   _recId,
    boolean _forUpdate = false)
{
    HMTreeSearchingTmp hmTreeSearchingTmp;
    if (_recId)
    {
        hmTreeSearchingTmp.selectForUpdate(_forUpdate);

        select firstonly hmTreeSearchingTmp
            where hmTreeSearchingTmp.RecId == _recId;
    }
    return hmTreeSearchingTmp;
}
