Method.Source:
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMTypeOfService::exist(_recId))
    {
        ret = checkFailed(strfmt(HMTypeOfService::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
/// Determines whether the specified record in the <c>HMTypeOfService</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMTypeOfService</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>

public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 HMTypeOfService
             where
                 HMTypeOfService.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///     Finds the specified record in the <c>HMTypeOfService</c> table.
/// </summary>
/// <param name="_RecId">
///     The record ID of the <c>HMTypeOfService</c> table to find.
/// </param>
/// <param name="_update">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMTypeOfService</c> table if found; otherwise, an empty buffer.
/// </returns>

public static HMTypeOfService find(RecId _RecId, boolean _update = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMTypeOfService HMTypeOfService;

    HMTypeOfService.selectForUpdate(_update);
    if(_Update  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        HMTypeOfService.concurrencyModel(_concurrencyModel);
    }
    select firstOnly * from HMTypeOfService where
        HMTypeOfService.RecId == _RecId;

    return HMTypeOfService;
}

/// <summary>
///    Gets the message to use when a specific record in the <c>HMTypeOfService</c> does not exist.
/// </summary>
/// <returns>
///    The message that states a record does not exist.
/// </returns>

public static TxtNotExist txtNotExist()
{
    return "@SYS127989";
}
