public RefreshFrequency xds()
{
    DirPersonUser                   dirPersonUser;
    DirPerson                       dirPerson;
    DirPartyTable                   dirPartyTable;
    VendTable                       vendTable;
    HcmWorker                       hcmWorker;
    HMResourceMaster                hmResourceMaster;
    HMResourceOrganizationalUnit    hmResourceOrgUnit;
    Query                           queryResource;
    HMResourceType                  hmResourceType;
    QueryRun                        queryRun;
    boolean                         isFirst = true;
    HMUserPlanningObjectOUXds       hmUserPlanningObjectOUXds;
    str resultRecid;


    select * from dirPersonUser
    join InstanceRelationType,RecId from dirPartyTable
    where dirPartyTable.RecId == dirPersonUser.PersonParty
    && dirPersonUser.User == curUserId();

    select firstOnly RecId from vendTable
        where vendTable.Party == dirPartyTable.RecId;
    if(vendTable.RecId)
    {
        hmResourceType = HMResourceType::Vendor;
    }

    select firstOnly RecId from hcmWorker
        where hcmWorker.Person == dirPersonUser.PersonParty;
    if(hcmWorker.RecId)
    {
        hmResourceType = HMResourceType::Employee;
    }
    queryResource = new Query();
    queryResource.addDataSource(tablenum(HMResourceSchedulingPlanningObject)).addDataSource(tableNum(HMOrganizationalUnit)).relations(true);
    queryResource.dataSourceTable(tableNum(HMOrganizationalUnit)).addRange(fieldNum(HMOrganizationalUnit,RecId)).value(SysQuery::value(0));
    if(vendTable.RecId || hcmWorker.RecId)
    {
        queryResource = new Query();
        queryResource.addDataSource(tableNum(HMResourceOrganizationalUnit)).addDataSource(tableNum(HMResourceMaster)).relations(true);
        queryResource.dataSourceTable(tableNum(hmResourceMaster)).addRange(fieldNum(HMResourceMaster,Type)).value(SysQuery::value(hmResourceType));
        queryResource.dataSourceTable(tableNum(hmResourceMaster)).addRange(fieldNum(HMResourceMaster,RefRecId)).value(SysQuery::value(hmResourceType == HMResourceType::Employee ? hcmWorker.RecId : vendTable.RecId));
        queryRun = new QueryRun(queryResource);
        while (queryRun.next())
        {
            hmResourceOrgUnit = queryRun.get(tableNum(HMResourceOrganizationalUnit));
            hmUserPlanningObjectOUXds.HMOrganizationalUnit = hmResourceOrgUnit.HMOrganizationalUnit;
            hmUserPlanningObjectOUXds.insert();
        }
    }

    return RefreshFrequency::PerSession;
}
