public void deleteAgent()
{
    HMClaimTable            hmClaimTable;
    FormRun                 fr = this as FormRun;
    CustCollectionsAgent    custCollectionsAgent = fr.dataSource(formDataSourceStr(CustCollectionsAgent, CustCollectionsAgent)).cursor();

    select firstonly RecId from HMClaimTable
        where HMClaimTable.ClaimAgent == custCollectionsAgent.RecId;

    if (HMClaimTable.RecId)
    {
        throw warning("@MAZ:DeleteAssignedAgentToClaim");
    }

    next deleteAgent();

}
