public void preRunModifyContract()
{
    HMAdjudicationHeader hmAdjudicationHeader;        
    QueryBuildDataSource queryBuildDataSource;
    Query query = this.getFirstQuery();

    if (this.parmArgs() != null)
    {
        hmAdjudicationHeader = this.parmArgs().record() as HMAdjudicationHeader;

        if (hmAdjudicationHeader.RecId == 0)
        {
            throw error(Error::missingRecord(tablePName(HMAdjudicationHeader)));
        }
        else
        {
            queryBuildDataSource = query.dataSourceTable(tableNum(HMAdjudicationHeader));
            queryBuildDataSource.addRange(fieldNum(HMAdjudicationHeader, TransNum)).value(queryValue(hmAdjudicationHeader.TransNum));
        }
    }
}

public static void main(Args _args)
{
    HMAdjudicationHeader   hmAdjudicationHeader;

    HMAdjudicationController controller = new HMAdjudicationController();

    controller.parmReportName(ssrsReportStr(HMAdjudicationReport, Report));
    controller.parmArgs(_args);
    controller.parmShowDialog(false);
    controller.startOperation();
}
