/// <summary>
/// 
/// </summary>
public void run()
{
    HMDataPrepForAllClaimExportMultiThreadBatchJob   hmDataPrepForAllClaimExportMultiThreadBatchJob;
    int                                                     recordsToProcess, perThreadRecordsRound, additionalRecordsRound;
    HMOneDecimal                                            additionalRecords;
    real                                                    perThreadRecords, diffPerThread;
    RecId                                                   hmClaimBatchJobLogsHeaderDataPrep, batchJobId;
    HMAllClaimExport                                        hmAllClaimExport;

    try
    {
        ttsbegin;

        delete_from hmAllClaimExport;


        ttscommit;
    }
    catch (Exception::Deadlock)
    {
        if (xSession::currentRetryCount() >= #RetryNum)
        {
            throw error(strFmt("@MAZ:FailedToDeleteDataFromTmpTable","HMAllClaimExport"));
        }
        else
        {
            retry;
        }
    }
    catch
    {
        if (xSession::currentRetryCount() >= #RetryNum)
        {
            throw error(strFmt("@MAZ:FailedToDeleteDataFromTmpTable","HMAllClaimExport"));
        }
        else
        {
            retry;
        }

    }

    try
    {

        if(this.isInBatch())
        {
            numberOfThreads = numberOfThreads == 0 ? 1 : numberOfThreads < 0 ? numberOfThreads * -1 : numberOfThreads;

            recordsToProcess = SysQuery::countLoops(gQueryRun);

            Info(strFmt("@MAZ:NoOfRecordsPicked", recordsToProcess));

            if(recordsToProcess <= numberOfThreads)
            {
                numberOfThreads = 1;
                Info("@MAZ:RecordsLesserThanNumberOfThreads");
            }

            perThreadRecords        = recordsToProcess / numberOfThreads;
            perThreadRecordsRound   = perThreadRecords;
            diffPerThread           = perThreadRecords - perThreadRecordsRound;
            additionalRecords       = diffPerThread * numberOfThreads;
            additionalRecords       = roundUp(additionalRecords, 1);
            additionalRecordsRound  = additionalRecords;

            if(perThreadRecordsRound + additionalRecordsRound > 0)
            {
                if(!batchHeader)
                {
                    batchJobId = this.parmCurrentBatch().BatchJobId;

                    batchHeader = BatchHeader::construct(batchJobId);
                }

                hmDataPrepForAllClaimExportMultiThreadBatchJob = HMDataPrepForAllClaimExportMultiThreadBatchJob::construct();
                hmDataPrepForAllClaimExportMultiThreadBatchJob.parmQueryRun(gQueryRun);
                hmDataPrepForAllClaimExportMultiThreadBatchJob.parmNumberOfThreads(numberOfThreads);
                hmDataPrepForAllClaimExportMultiThreadBatchJob.parmPerThreadRecordsRound(perThreadRecordsRound);
                hmDataPrepForAllClaimExportMultiThreadBatchJob.parmAdditionalRecords(additionalRecordsRound);
                hmDataPrepForAllClaimExportMultiThreadBatchJob.parmTaskDescription("@MAZ:DataPreparation");

                hmClaimBatchJobLogsHeaderDataPrep = HMClaimBatchJobLogsHeader::insertData(HMClaimJobType::AllClaimExport//need to change with allclaimexport
                                                                                , batchJobId
                                                                                , recordsToProcess
                                                                                , numberOfThreads);

                hmDataPrepForAllClaimExportMultiThreadBatchJob.parmHMClaimBatchJobLogsHeader(hmClaimBatchJobLogsHeaderDataPrep);

                batchHeader.addRuntimeTask(hmDataPrepForAllClaimExportMultiThreadBatchJob, this.parmCurrentBatch().RecId);

            }
            else
            {
                Info("@MAZ:NoRecordsFoundForProcessing");
            }

        }
        else
        {
            // execute your code here when not running in batch
            try
            {
                HMDataPrepForAllClaimExportBusinessLogic hmDataPrepForAllClaimExportBusinessLogic = HMDataPrepForAllClaimExportBusinessLogic::construct();


                hmClaimBatchJobLogsHeaderDataPrep = HMClaimBatchJobLogsHeader::insertData(HMClaimJobType::AllClaimExport
                                                                                , 0
                                                                                , recordsToProcess
                                                                                , numberOfThreads);

                hmDataPrepForAllClaimExportBusinessLogic.processInit(gQueryRun, 0, hmClaimBatchJobLogsHeaderDataPrep);
            }
            catch
            {
                warning(strFmt("%1",xSession::xppCallStack()));
            }
        }

        if(batchHeader)
        {
            // save the batchheader with added tasks
            batchHeader.save();//BatchStatus::Hold);
        }

    }
    catch
    {
        throw error(strFmt("@MAZ:BatchJobInitiationFailed",xSession::xppCallStack()));
    }
}

//Needs to be set to true so class can be ran in a batch
boolean canGoBatch()
{
    return true;
}

//Stores parameters of the batch
public container pack()
{
    container pack = conNull();
    if (gQueryRun)
    {
        pack = gQueryRun.pack();
    }
    return [#CurrentVersion] + [pack] + [#CurrentList];
}

//Returns the stored object for the batch to use
public boolean unpack(container packedClass)
{
    boolean     ret         = false;
    int         version     = RunBase::getVersion(packedClass);
    container   packedQuery = conNull();

    switch (version)
    {
        case #CurrentVersion:
            [version, packedQuery, #CurrentList] = packedClass;

            if (SysQuery::isPackedOk(packedQuery))
            {
                gQueryRun   = new QueryRun(packedQuery);
                ret         = true;
            }
            break;

        default:
            ret = false;
    }
    return ret;
}

//Determines whether to run on server or client
//True - Server; False - Client
public boolean runsImpersonated()
{
    return true;
}

/// <summary>
/// 
/// </summary>
