public static void main(Args args)
{
    HMAllClaimExportMainMultiThreadBatchJob hmAllClaimExportMainMultiThreadBatchJob = HMAllClaimExportMainMultiThreadBatchJob::construct();

    hmAllClaimExportMainMultiThreadBatchJob.caption();
    hmAllClaimExportMainMultiThreadBatchJob.initQueryRun();

    if (hmAllClaimExportMainMultiThreadBatchJob.prompt())
    {
        hmAllClaimExportMainMultiThreadBatchJob.run();
    }

}

