public void jumpref()
{
    next jumpref();
    Args                args = new Args();
    MenuFunction        menuFunction;
    HMPatient           hmPatient;
    FormDataObject      formDataObject;
    CustTable           custTable;

    formDataObject = any2Object(this) as FormDataObject;
    custTable = formDataObject.datasource().cursor();
    hmPatient = hmPatient::findByMRN(custTable.AccountNum);
    args.record(hmPatient);
    args.lookupRecord(hmPatient);
    args.caller(formDataObject.datasource().formRun());
    args.lookupTable(tableNum(hmPatient));
    menuFunction = new MenuFunction(menuItemDisplayStr(HMAllPatientsListPage), MenuItemType::Display);
    menuFunction.run(args);
}
