public void executeQuery()
{
    HMClaimTable hmClaimTable = this.formRun().dataSource(formDataSourceStr(HMAllClaims, HMClaimTable)).cursor();

    this.query().dataSourceName("PatientClaims").clearRange(fieldNum(HMClaimTable, HMInsuranceCarrier));
    this.query().dataSourceName("PatientClaims").addRange(fieldnum(HMClaimTable,HMInsuranceCarrier)).value(SysQuery::value(hmClaimTable.HMInsuranceCarrier));

    next executeQuery();
}
