/// <summary>
///
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[FormControlEventHandler(formControlStr(HMAllClaims, ViewClaim), FormControlEventType::Clicked)]
public static void ViewClaim_OnClicked(FormControl sender, FormControlEventArgs e)
{
    FormDataSource  fds;
    HMClaimTable    hmClaimTable;
    MenuFunction    menuFunction;
    Args            args;

    args = new Args();
    fds = sender.formRun().dataSource(formDataSourceStr(HMAllClaims,PatientClaims));
    hmClaimTable = fds.cursor();

    args.parm("SelfClaim");
    args.record(hmClaimTable);
    args.caller(sender);
    menuFunction = new MenuFunction(menuItemDisplayStr(HMAllClaims), MenuItemType::Display);
    menuFunction.run(args);
}
