/// <summary>
/// 
/// </summary>
public void run()
{
    HMDataPrepForAssignPriorityToClaimMultiThreadBatchJob   hmDataPrepForAssignPriorityToClaimMultiThreadBatchJob;
    HMAssignPriorityToClaimsMultiThreadBatchJob             hmAssignPriorityToClaimsMultiThreadBatchJob;
    int                                                     recordsToProcess, perThreadRecordsRound, additionalRecordsRound;
    HMOneDecimal                                            additionalRecords;
    real                                                    perThreadRecords, diffPerThread;
    RecId                                                   hmClaimBatchJobLogsHeader, hmClaimBatchJobLogsHeaderDataPrep, batchJobId;
    HMClaimPrioritizationCalculatedFields                   hmClaimPrioritizationCalculatedFields;

    try
    {
        ttsbegin;

        delete_from hmClaimPrioritizationCalculatedFields
            where hmClaimPrioritizationCalculatedFields.CreatedDateTime <= DateTimeUtil::addDays(DateTimeUtil::utcNow(), -2);

        ttscommit;
    }
    catch (Exception::Deadlock)
    {
        if (xSession::currentRetryCount() >= #RetryNum)
        {
            throw error(strFmt("@MAZ:FailedToDeleteDataFromTmpTable","HMClaimPrioritizationCalculatedFields"));
        }
        else
        {
            retry;
        }
    }
    catch
    {
        if (xSession::currentRetryCount() >= #RetryNum)
        {
            throw error(strFmt("@MAZ:FailedToDeleteDataFromTmpTable","HMClaimPrioritizationCalculatedFields"));
        }
        else
        {
            retry;
        }

    }

    try
    {

        if(this.isInBatch())
        {
            numberOfThreads = numberOfThreads == 0 ? 1 : numberOfThreads < 0 ? numberOfThreads * -1 : numberOfThreads;

            recordsToProcess = SysQuery::countLoops(gQueryRun);

            Info(strFmt("@MAZ:NoOfRecordsPicked", recordsToProcess));

            if(recordsToProcess <= numberOfThreads)
            {
                numberOfThreads = 1;
                Info("@MAZ:RecordsLesserThanNumberOfThreads");
            }

            perThreadRecords        = recordsToProcess / numberOfThreads;
            perThreadRecordsRound   = perThreadRecords;
            diffPerThread           = perThreadRecords - perThreadRecordsRound;
            additionalRecords       = diffPerThread * numberOfThreads;
            additionalRecords       = roundUp(additionalRecords, 1);
            additionalRecordsRound  = additionalRecords;

            if(perThreadRecordsRound + additionalRecordsRound > 0)
            {
                if(!batchHeader)
                {
                    batchJobId = this.parmCurrentBatch().BatchJobId;

                    batchHeader = BatchHeader::construct(batchJobId);

                    batchHeader.parmRetriesOnFailure(5);
                }

                hmDataPrepForAssignPriorityToClaimMultiThreadBatchJob = HMDataPrepForAssignPriorityToClaimMultiThreadBatchJob::construct();
                hmDataPrepForAssignPriorityToClaimMultiThreadBatchJob.parmQueryRun(gQueryRun);
                hmDataPrepForAssignPriorityToClaimMultiThreadBatchJob.parmNumberOfThreads(numberOfThreads);
                hmDataPrepForAssignPriorityToClaimMultiThreadBatchJob.parmPerThreadRecordsRound(perThreadRecordsRound);
                hmDataPrepForAssignPriorityToClaimMultiThreadBatchJob.parmAdditionalRecords(additionalRecordsRound);
                hmDataPrepForAssignPriorityToClaimMultiThreadBatchJob.parmTaskDescription("@MAZ:DataPreparation");

                hmClaimBatchJobLogsHeaderDataPrep = HMClaimBatchJobLogsHeader::insertData(HMClaimJobType::PriorityDataPrep
                                                                                , batchJobId
                                                                                , recordsToProcess
                                                                                , numberOfThreads);

                hmDataPrepForAssignPriorityToClaimMultiThreadBatchJob.parmHMClaimBatchJobLogsHeader(hmClaimBatchJobLogsHeaderDataPrep);

                hmAssignPriorityToClaimsMultiThreadBatchJob = HMAssignPriorityToClaimsMultiThreadBatchJob::construct();
                hmAssignPriorityToClaimsMultiThreadBatchJob.parmNumberOfThreads(numberOfThreads);
                hmAssignPriorityToClaimsMultiThreadBatchJob.parmTaskDescription("@MAZ:AssignmentOfPriorityOnClaim");
                hmAssignPriorityToClaimsMultiThreadBatchJob.parmHMClaimBatchJobLogsHeaderDataPrep(hmClaimBatchJobLogsHeaderDataPrep);

                hmClaimBatchJobLogsHeader = HMClaimBatchJobLogsHeader::insertData(HMClaimJobType::Priority
                                                                                , batchJobId
                                                                                , recordsToProcess
                                                                                , numberOfThreads);

                hmAssignPriorityToClaimsMultiThreadBatchJob.parmHMClaimBatchJobLogsHeader(hmClaimBatchJobLogsHeader);

                batchHeader.addRuntimeTask(hmDataPrepForAssignPriorityToClaimMultiThreadBatchJob, this.parmCurrentBatch().RecId);
                batchHeader.addRuntimeTask(hmAssignPriorityToClaimsMultiThreadBatchJob, this.parmCurrentBatch().RecId);

                batchHeader.addDependency(hmAssignPriorityToClaimsMultiThreadBatchJob, hmDataPrepForAssignPriorityToClaimMultiThreadBatchJob);
            }
            else
            {
                Info("@MAZ:NoRecordsFoundForProcessing");
            }

        }
        else
        {
            // execute your code here when not running in batch
            try
            {
                HMDataPrepForAssignPriorityToClaimBusinessLogic hmDataPrepForAssignPriorityToClaimBusinessLogic = HMDataPrepForAssignPriorityToClaimBusinessLogic::construct();
                HMAssignPriorityToClaimsBusinessLogic           hmAssignPriorityToClaimsBusinessLogic = HMAssignPriorityToClaimsBusinessLogic::construct();

                hmClaimBatchJobLogsHeaderDataPrep = HMClaimBatchJobLogsHeader::insertData(HMClaimJobType::PriorityDataPrep
                                                                                , 0
                                                                                , recordsToProcess
                                                                                , numberOfThreads);

                hmDataPrepForAssignPriorityToClaimBusinessLogic.processInit(gQueryRun, 0, hmClaimBatchJobLogsHeaderDataPrep);

                hmClaimBatchJobLogsHeader = HMClaimBatchJobLogsHeader::insertData(HMClaimJobType::Priority
                                                                                , 0
                                                                                , recordsToProcess
                                                                                , numberOfThreads);

                hmAssignPriorityToClaimsBusinessLogic.processInit(0, hmClaimBatchJobLogsHeader, hmClaimBatchJobLogsHeaderDataPrep);
            }
            catch
            {
                warning(strFmt("%1",xSession::xppCallStack()));
            }
        }

        if(batchHeader)
        {
            // save the batchheader with added tasks
            batchHeader.save();//BatchStatus::Hold);
        }

    }
    catch
    {
        throw error(strFmt("@MAZ:BatchJobInitiationFailed",xSession::xppCallStack()));
    }
}

//Needs to be set to true so class can be ran in a batch
boolean canGoBatch()
{
    return true;
}

//Stores parameters of the batch
public container pack()
{
    container pack = conNull();
    if (gQueryRun)
    {
        pack = gQueryRun.pack();
    }
    return [#CurrentVersion] + [pack] + [#CurrentList];
}

//Returns the stored object for the batch to use
public boolean unpack(container packedClass)
{
    boolean     ret         = false;
    int         version     = RunBase::getVersion(packedClass);
    container   packedQuery = conNull();

    switch (version)
    {
        case #CurrentVersion:
            [version, packedQuery, #CurrentList] = packedClass;

            if (SysQuery::isPackedOk(packedQuery))
            {
                gQueryRun   = new QueryRun(packedQuery);
                ret         = true;
            }
            break;

        default:
            ret = false;
    }
    return ret;
}

//Determines whether to run on server or client
//True - Server; False - Client
public boolean runsImpersonated()
{
    return true;
}

/// <summary>
/// 
/// </summary>
/// <param name = "args"></param>
public static void main(Args args)
{
    HMAssignPriorityToClaimMainMultiThreadBatchJob hmAssignPriorityToClaimMainMultiThreadBatchJob = HMAssignPriorityToClaimMainMultiThreadBatchJob::construct();

    hmAssignPriorityToClaimMainMultiThreadBatchJob.caption();
    hmAssignPriorityToClaimMainMultiThreadBatchJob.initQueryRun();

    if (hmAssignPriorityToClaimMainMultiThreadBatchJob.prompt())
    {
        hmAssignPriorityToClaimMainMultiThreadBatchJob.run();
    }

}

