/// false.
/// </returns>
protected boolean canRunInNewSession()
{
    return false;
}

/// <summary>
/// This method initiated the task for assignment of pool batch job for $0 denied claims
/// </summary>
public void run()
{
    HMCalculateZeroClaimPoolMultiThreadTask hmCalculateZeroClaimPoolMultiThreadTask;

    try
    {
        if(this.isInBatch())
        {
            for(int i = 1; i <= numberOfThreads; i++)
            {
                if(!batchHeader)
                {
                    batchHeader = BatchHeader::construct(this.parmCurrentBatch().BatchJobId);
                }

                hmCalculateZeroClaimPoolMultiThreadTask = HMCalculateZeroClaimPoolMultiThreadTask::construct();
                hmCalculateZeroClaimPoolMultiThreadTask.parmTaskDescription(strFmt("@Optum:AssignmentOfPoolsOnZeroClaimsMultipleThreadTask", i));
                hmCalculateZeroClaimPoolMultiThreadTask.parmTaskNumber(i);
                hmCalculateZeroClaimPoolMultiThreadTask.parmHMClaimBatchJobLogsHeader(hmClaimBatchJobLogsHeader);
                hmCalculateZeroClaimPoolMultiThreadTask.parmHMClaimBatchJobLogsHeaderDataPrep(hmClaimBatchJobLogsHeaderDataPrep);

                // add tasks to the batch header
                batchHeader.addRuntimeTask(hmCalculateZeroClaimPoolMultiThreadTask, this.parmCurrentBatch().RecId);
            }

        }

        if(batchHeader)
        {
            // save the batchheader with added tasks
            batchHeader.save();
        }

    }
    catch
    {
        throw Error(strFmt("@MAZ:PoolAssignmentBatchJobFailed", xSession::xppCallStack()));
    }

}

//Stores parameters of the batch
public container pack()
{
    return [#CurrentVersion] + [#NumberOfThreads] + [#TaskDescription] + [#HMClaimBatchJobLogsHeader] + [#HMClaimBatchJobLogsHeaderDataPrep];
}

//Returns the stored object for the batch to use
public boolean unpack(container packedClass)
{
    boolean     ret     = true;
    int         version = RunBase::getVersion(packedClass);

    switch (version)
    {
        case #CurrentVersion:
            [version, #NumberOfThreads, #TaskDescription, #HMClaimBatchJobLogsHeader, #HMClaimBatchJobLogsHeaderDataPrep] = packedClass;
            break;
        default:
            ret = false;
    }

    return ret;
}

