Method.Source:
public void init()
{
    QueryBuildDataSource hmClaimTableListPageWorkspaceView, hmClaimNotes,hmClaimTable_qbds;

    next init();

    this.index(indexNum(HMClaimTable, ClaimPriorityAgentPoolIdx));

    hmClaimTable_qbds = this.queryBuildDataSource();

    hmClaimTable_qbds.clearRange(fieldNum(HMClaimTable, HMBalanceAmount));

    hmClaimTable_qbds.addRange(fieldNum(HMClaimTable, HMClaimPriority)).value(SysQuery::valueNot(0));

    if(element.isMultiSelectPoolFilterEnabled)
    {
        hmClaimTableListPageWorkspaceView = this.queryBuildDataSource();

        hmClaimTableListPageWorkspaceView.addSortField(fieldNum(HMClaimTable, HMClaimPriority),SortOrder::Ascending);

        if (element.args().menuItemName() == menuitemDisplayStr(HMClaimsofLast30DaysWithNotesorAction))
        {
            hmClaimNotes = hmClaimTableListPageWorkspaceView.addDataSource(tableNum(HMClaimNotes));
            hmClaimNotes.addLink(fieldNum(HMClaimTable, RecId), fieldNum(HMClaimNotes, HMClaimTable));
            hmClaimNotes.joinMode(JoinMode::ExistsJoin);

            hmClaimNotes.addRange(fieldNum(HMClaimNotes, CreatedDateTime)).value(queryRange(DateTimeUtil::addDays(DateTimeUtil::utcNow(),-30),DateTimeUtil::utcNow()));
        }
        else if (element.args().menuItemName() == menuitemDisplayStr(HMClaimsofNext7DaysFollowUpDate))
        {
            hmClaimTableListPageWorkspaceView.addRange(fieldNum(HMClaimTable, HMFollowUp)).value(queryRange(DateTimeUtil::utcNow(),DateTimeUtil::addDays(DateTimeUtil::utcNow(),7)));
        }
        else if (element.args().menuItemName() == menuitemDisplayStr(HMClaimsWithNoActionsOrNote))
        {
            hmClaimNotes = hmClaimTableListPageWorkspaceView.addDataSource(tableNum(HMClaimNotes));
            hmClaimNotes.addLink(fieldNum(HMClaimTable, RecId), fieldNum(HMClaimNotes, HMClaimTable));
            hmClaimNotes.joinMode(JoinMode::NoExistsJoin);
        }
        else if (element.args().menuItemName() == menuitemDisplayStr(HClaimAgentClaimPreviouslyWorkedOn))
        {
            hmClaimTableListPageWorkspaceView.clearRange(fieldNum(HMClaimTable, HMBalanceAmount));
            hmClaimTableListPageWorkspaceView.addRange(fieldNum(HMClaimTable, HMBalanceAmount)).value(queryValue(0));

            hmClaimNotes = hmClaimTableListPageWorkspaceView.addDataSource(tableNum(HMClaimNotes));
            hmClaimNotes.addLink(fieldNum(HMClaimTable, RecId), fieldNum(HMClaimNotes, HMClaimTable));
            hmClaimNotes.joinMode(JoinMode::ExistsJoin);

            hmClaimNotes.addRange(fieldNum(HMClaimNotes, CreatedDateTime)).value(queryRange(DateTimeUtil::addDays(DateTimeUtil::utcNow(),-60),DateTimeUtil::utcNow()));
        }
        else if (element.args().menuItemName() != menuitemDisplayStr(HMClaimAgentViewActiveClaims))
        {
            if(formHasMethod(element.args().caller(), identifierStr(setAssignedClaims)))
            {
                element.setAssignedClaims(true);
            }

        }

    }

}
