public void buildTempTable()
{
    next buildTempTable();

    HMClaimTable		        hmClaimTable;
    CustCollectionsAgentPool    custCollectionsAgentPool;
    CustCollectionsPool         custCollectionsPool;
    HMInsuranceCarrier          HMInsuranceCarrier;
    mzkClaimManagerChartDataTmp _bucket;
    CustCollectionsAgent        _gCustCollectionsAgent;

    _bucket = this.bucket;
    _gCustCollectionsAgent = this.gCustCollectionsAgent;

    delete_from _bucket;

    insert_recordset _bucket(ClaimCount,Status)
        select count(RecId) from hmClaimTable
            join HMPayerType from hmInsuranceCarrier
            group by HMPayerType
                where hmInsuranceCarrier.RecId == hmClaimTable.hmInsuranceCarrier
            join custCollectionsPool
                where custCollectionsPool.RecId == hmClaimTable.ClaimPool
            join custCollectionsAgentPool
                where custCollectionsAgentPool.CustomerPoolId == custCollectionsPool.Name
                    && custCollectionsAgentPool.CustCollectionsAgent == _gCustCollectionsAgent.RecId;

    mzkClaimManagerChartDataTmp.linkPhysicalTableInstance(_bucket);
}
