public static str getSupervisor()
{
    str supervisor;

    supervisor = strFmt(@"SELECT TOP 1 P.NAME FROM DIRPARTYTABLE AS P WHERE P.RecId = %1 ORDER BY RECID DESC",
        SysComputedColumn::returnField(tableStr(HMClaimTableView), identifierStr(CustCollectionsAgent), fieldStr(CustCollectionsAgent, HMSupervisor)));

    return supervisor;
}

public static str getInvoiceType()
{
    str invoiceType;

    invoiceType = strFmt(@"SELECT TOP 1 CONVERT(nvarchar(100), CONCAT(DESCRIPTION,' (',Name,')')) as INVOICETYPE FROM HMINVOICETYPE WHERE NAME = %1",
        SysComputedColumn::returnField(tableStr(HMClaimTableView), identifierStr(HMClaimTable), fieldStr(HMClaimTable, HMInvoiceType)));

    return invoiceType;
}

