public static str getClaimLastPaymentPostingDeniedDate()
{
    str claimLastPaymentPostingDeniedDate;

    claimLastPaymentPostingDeniedDate = strFmt(@"SELECT TOP 1 PP.DATEPOSTED FROM HMCLAIMPAYMENTPOSTING AS PP WHERE PP.CLAIMID = %1 AND PP.TYPE = 'DENIAL' ORDER BY RECID DESC",
        SysComputedColumn::returnField(tableStr(HMClaimTableView), identifierStr(HMClaimTable), fieldStr(HMClaimTable, ClaimID)));

    return claimLastPaymentPostingDeniedDate;
}
