	public void init()
    {
        next init();

        Query                query;
        QueryBuildDataSource qbdsDirPartyRelationship,qbdsDirPartyTable;

        query = this.query();

        qbdsDirPartyRelationship = query.dataSourceTable(tableNum(DirPartyRelationship));

        qbdsDirPartyTable = qbdsDirPartyRelationship.addDataSource(tableNum(DirPartyTable));
        qbdsDirPartyTable.joinMode(JoinMode::InnerJoin);
        qbdsDirPartyTable.addLink(fieldNum(DirPartyRelationship, ChildParty),fieldNum(DirPartyTable,RecId));

        qbdsDirPartyTable.addSortField(fieldNum(DirPartyTable, Name), SortOrder::Ascending);
    }
