SourceCode.Declaration:
class HMCustCollectionsPoolSequencing
{
    HMCustCollectionsSequenceNumberTmp  sequenceNumberTmp;
    CustCollectionsPool                 custCollectionsPool;

}

Method.Source:
public static void updateSequencing(real _newValue, real _oldValue)
{
    HMCustCollectionsPoolSequencing obj = new HMCustCollectionsPoolSequencing();

    if(_newValue < _oldValue)
    {
        obj.moveDown(_newValue, _oldValue);
    }
    else
    {
        obj.moveUp(_newValue, _oldValue);
    }

    obj.updateCustCollectionsPool();
}

	public void moveDown(real _newValue, real _oldValue)
    {
        for (int i = _newValue; i < _oldValue; i++)
        {
            select firstonly RecId from custCollectionsPool
                where custCollectionsPool.HMSequenceNumber == i;

            if(custCollectionsPool)
            {
                sequenceNumberTmp.SequenceNumber = i + 1;
                sequenceNumberTmp.RefRecId = custCollectionsPool.RecId;
                sequenceNumberTmp.insert();
            }

        }

    }

public void moveUp(real _newValue, real _oldValue)
{
    for (int i = _oldValue; i < _newValue; i++)
    {
        select firstonly RecId from custCollectionsPool
            where custCollectionsPool.HMSequenceNumber == i + 1;

        if(custCollectionsPool)
        {
            sequenceNumberTmp.SequenceNumber = i;
            sequenceNumberTmp.RefRecId = custCollectionsPool.RecId;
            sequenceNumberTmp.insert();
        }
    }

}

public void updateCustCollectionsPool()
{
    CustCollectionsPool updateCustCollectionsPool;

    ttsbegin;

    update_recordset updateCustCollectionsPool
        setting HMSequenceNumber = sequenceNumberTmp.SequenceNumber
        join sequenceNumberTmp
            where updateCustCollectionsPool.RecId == sequenceNumberTmp.RefRecId;

    ttscommit;
}
