/// <summary>
/// This is an extension class for the form <c>DMFDataVizualization</c> and control <c>showErrorMessageHM</c>
/// </summary>
[ExtensionOf(formControlStr(DMFDataVizualization, showErrorMessageHM))]
internal final class HMDMFDataVizualizationShowError_Extension
{
}

Method.Source:
/// <summary>
/// Modified the show error checkbox.
/// </summary>
/// <returns>Boolean</returns>
public boolean modified()
{
    QueryBuildRange     qbRange;
    SysDictTable        dictTable;
    boolean             ret;
    FormDataSource      formDataSource;
    FormControl         showErrorMsgControl = this as FormControl;

    ret = next modified();

    dictTable = new SysDictTable(element.form().dataSource(1).table());
    formDataSource = showErrorMsgControl.formRun().dataSource(1);

    if (this.value() == (NoYes::Yes))
    {
        formDataSource.queryBuildDataSource().clearRange(dictTable.fieldName2Id(identifierStr(TransferStatus)));
        qbRange = formDataSource.queryBuildDataSource().addRange(dictTable.fieldName2Id(identifierStr(TransferStatus)));
        qbRange.value(SysQuery::value(DMFTransferStatus::Error));
        qbRange.status(RangeStatus::Locked);

        // Enable the message field on grid.
        showErrorMsgControl.formRun().design().controlName(formControlStr(DMFDataVizualization, HMShowErrorMessageGrid)).visible(true);
    }
    else
    {
        formDataSource.queryBuildDataSource().clearRange(dictTable.fieldName2Id(identifierStr(TransferStatus)));
        qbRange = formDataSource.queryBuildDataSource().addRange(dictTable.fieldName2Id(identifierStr(TransferStatus)));
        qbRange.value(SysQuery::valueNot(DMFTransferStatus::Duplicate));
        qbRange.status(RangeStatus::Locked);

        // Disable the message field on grid.
        showErrorMsgControl.formRun().design().controlName(formControlStr(DMFDataVizualization, HMShowErrorMessageGrid)).visible(false);
    }

    formDataSource.executeQuery();

    return ret;
}
