    return new HMDataPrepForAssignPriorityToZeroClaimMultiThreadBatchJob();
}

/// <summary>
/// Describes whether the class is designed for execution in a new session.
/// </summary>
/// <returns>
/// false.
/// </returns>
protected boolean canRunInNewSession()
{
    return false;
}

/// <summary>
/// Description of the task
/// </summary>
/// <returns>Return description of the task</returns>
static ClassDescription description()
{
    return taskDescription;
}

/// <summary>
/// This method initiated the task to generate the data in data preparation table
/// </summary>
public void run()
{
    HMDataPrepForAssignPriorityToZeroClaimMultiThreadTask hmDataPrepForAssignPriorityToZeroClaimMultiThreadTask;
    int                                                   queryStartPosition, queryEndPosition;

    try
    {
        if(this.isInBatch())
        {
            for(int i = 1; i <= numberOfThreads; i++)
            {
                if(!batchHeader)
                {
                    batchHeader = BatchHeader::construct(this.parmCurrentBatch().BatchJobId);
                }

                if(i == 1)
                {
                    queryStartPosition  = 1;
                    queryEndPosition    = perThreadRecordsRound + additionalRecords;
                }
                else
                {
                    queryStartPosition  = queryEndPosition + 1;
                    queryEndPosition    = queryEndPosition + perThreadRecordsRound;
                }

                hmDataPrepForAssignPriorityToZeroClaimMultiThreadTask = HMDataPrepForAssignPriorityToZeroClaimMultiThreadTask::construct();
                hmDataPrepForAssignPriorityToZeroClaimMultiThreadTask.parmQueryRun(gQueryRun);
                hmDataPrepForAssignPriorityToZeroClaimMultiThreadTask.parmQueryStartPosition(queryStartPosition);
                hmDataPrepForAssignPriorityToZeroClaimMultiThreadTask.parmPerThreadRecords(i == 1 ? perThreadRecordsRound + additionalRecords : perThreadRecordsRound);
                hmDataPrepForAssignPriorityToZeroClaimMultiThreadTask.parmTaskDescription(strFmt("@MAZ:DataPreparationForClaimPriorityMultipleThreadTask", i, queryStartPosition, queryEndPosition));
                hmDataPrepForAssignPriorityToZeroClaimMultiThreadTask.parmTaskNumber(i);
                hmDataPrepForAssignPriorityToZeroClaimMultiThreadTask.parmHMClaimBatchJobLogsHeader(hmClaimBatchJobLogsHeader);

                // add tasks to the batch header
                batchHeader.addRuntimeTask(hmDataPrepForAssignPriorityToZeroClaimMultiThreadTask, this.parmCurrentBatch().RecId);

            }

        }

        if(batchHeader)
        {
            // save the batchheader with added tasks
            batchHeader.save();
        }

    }
    catch
    {
        throw error(strFmt("@MAZ:DataPreparationBatchJobFailedError",xSession::xppCallStack()));
    }
}

//Stores parameters of the batch
public container pack()
{
    container pack = conNull();

    if (gQueryRun)
    {
        pack = gQueryRun.pack();
    }

    return [#CurrentVersion] + [pack] + [#NumberOfThreads] + [#PerThreadRecordsRound] + [#AdditionalRecords] + [#TaskDescription] + [#hmClaimBatchJobLogsHeader];
}

//Returns the stored object for the batch to use
public boolean unpack(container packedClass)
{
    boolean     ret         = false;
    int         version     = RunBase::getVersion(packedClass);
    container   packedQuery = conNull();

    switch (version)
    {
        case #CurrentVersion:
            [version, packedQuery, #NumberOfThreads, #PerThreadRecordsRound, #AdditionalRecords, #TaskDescription, #hmClaimBatchJobLogsHeader] = packedClass;

            if (SysQuery::isPackedOk(packedQuery))
            {
                gQueryRun   = new QueryRun(packedQuery);
                ret         = true;
            }
            break;
        default:
            ret = false;
    }

    return ret;
}

