/// <summary>
/// Construct the class
/// </summary>
/// <returns>Return the class object</returns>
public server static HMDataPrepForZeroClaimAgeCalcBusinessLogic construct()
{
    return new HMDataPrepForZeroClaimAgeCalcBusinessLogic();
}

public QueryRun addZeroClaimRange(QueryRun _queryRun)
{
    Query                query;
    QueryBuildDataSource qbds;

    this.jobType                    = HMClaimJobType::AgeZeroBalanceDataPrep;
    this.jobTypeForCalcAgeBatchJob = HMClaimJobType::AgeZeroBalance;

    query = _queryRun.query();

    qbds = query.dataSourceTable(tableNum(HMClaimTable)).addDataSource(tableNum(HMZeroClaimBatchJobStatus));
    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.relations(false);
    qbds.addLink(fieldnum(HMClaimTable, ClaimID), fieldnum(HMZeroClaimBatchJobStatus, ClaimID));

    _queryRun.query(query);

    return _queryRun;
}
