Method.Source:
[SysClientCacheDataMethodAttribute(true)]
public display HMPayerName HMPayerName()
{
    HMPatientInsurance  hmPatientInsurance;
    HMInsuranceCarrier  hmInsuranceCarrier;

    hmPatientInsurance = this.cursor();

    select firstonly hmInsuranceCarrier
        where hmInsuranceCarrier.Code == hmPatientInsurance.InsuranceCarrier;

    return hmInsuranceCarrier.HMPayerType;
}
