/// <summary>
/// Replaces the hardcoded string with the customer account number.
/// </summary>
/// <param name = "_mrnNo">Customer account number.</param>
/// <returns>Returns the replaced url string.</returns>
public str replaceURLString(str _mrnNo)
{
    HMBillParameters hmBillParameters;
    str              url;

    select HMPatientPortalLink from hmBillParameters;

    url = hmBillParameters.HMPatientPortalLink;

    url = strReplace(url,'{mrn}',_mrnNo);

    return url;
}

/// <summary>
/// Class entry point. The system will call this method when a designated menu 
/// is selected or when execution starts and this class is set as the startup class.
/// </summary>
/// <param name = "_args">The specified arguments.</param>
public static void main(Args _args)
{
    HMPatientManagmentPortalLinkUpdate hmPatientPortalLink;
    CustTable                          custTable;
    str                                urlString;

    custTable = _args.record();

    hmPatientPortalLink = new HMPatientManagmentPortalLinkUpdate();
    urlString = hmPatientPortalLink.replaceURLString(custTable.AccountNum);

    if(urlString == '')
    {
        throw warning("@Optum:HMPatientPortalUnavailable");
    }
    else
    {
        new Browser().navigate(urlString,true);
    }
}
