public boolean validateWrite()
{
    boolean         ret;
    str             dialogMsg = "@Optum:UpdateClaimCacheFieldsConfirmationMsg";
    str             dialogTitle = "@SYS4006395";
    DirPersonName   personName = this.cursor();
    Name            oldLastName, oldFirstName, oldMiddleName;

    oldLastName     = personName.orig().LastName;
    oldFirstName    = personName.orig().FirstName;
    oldMiddleName   = personName.orig().MiddleName;

    ret = next validateWrite();

    if(oldLastName != personName.LastName || oldFirstName != personName.FirstName || oldMiddleName != personName.MiddleName)
    {
        if(Box::yesNo(dialogMsg ,DialogButton::Yes ,dialogTitle) == DialogButton::No)
        {
            ret = false;
        }
    }

    return ret;
}
