public int active()
{   
    int ret;

    ret = next active();

    HMInsuranceCarrier hmInsuranceCarrier = this.cursor();

    this.object(fieldNum(HMInsuranceCarrier, HMInActiveDate)).allowEdit(hmInsuranceCarrier.HMInactive);

    return ret;
}

[SysClientCacheDataMethodAttribute(true)]
public display HMRegionName RegionName()
{
    HMSiteTable         hmSite;
    HMInsuranceCarrier  hmInsuranceCarrier;

    hmInsuranceCarrier = this.cursor();

    select firstonly hmSite
        where hmSite.HMSiteName == hmInsuranceCarrier.HMSite;

    return hmSite.HMRegion;
}
