/// <summary>
/// This method initiated the task for calculation and updation of aging days batch job for $0 denied claims
/// </summary>
public void run()
{

    try
    {
        HMClaimAgeCalculateBusinessLogic hmClaimAgeCalculateBusinessLogic = HMClaimAgeCalculateBusinessLogic::construct();

        hmClaimAgeCalculateBusinessLogic.processInit(taskNumber, hmClaimBatchJobLogsHeader, hmClaimBatchJobLogsHeaderDataPrep);
    }
    catch
    {
        throw error(strFmt("@MAZ:AgeAssignmentBatchJobFailed", taskNumber, xSession::xppCallStack()));
    }

}

//Stores parameters of the batch
public container pack()
{
    return [#CurrentVersion] + [#TaskDescription] + [#TaskNumber] + [#HMClaimBatchJobLogsHeader] + [#HMClaimBatchJobLogsHeaderDataPrep];
}

//Returns the stored object for the batch to use
public boolean unpack(container packedClass)
{
    boolean     ret     = true;
    int         version = RunBase::getVersion(packedClass);

    switch (version)
    {
        case #CurrentVersion:
            [version, #TaskDescription, #TaskNumber, #HMClaimBatchJobLogsHeader, #HMClaimBatchJobLogsHeaderDataPrep] = packedClass;
            break;
        default:
            ret = false;
    }

    return ret;
}

